"use strict";app.directive("arrow",["$window",function($window){return function(scope){angular.element($window).bind("scroll",function(){var w=angular.element($window),topWindow=w.scrollTop(),topWindow=1.5*topWindow,windowHeight=w.height(),position=topWindow/windowHeight;position=1-position,$(".demo-section .arrow-wrap").css("opacity",position),w.bind("arrow",function(){scope.$apply()})})}}]);